mapper.select:

select subj_code, subj_nm, subj_regdate, subj_startdate, subj_enddate
from
	(
	select /*+INDEX(subject idx_subject) */
		rownum rn, subj_code, subj_nm, subj_regdate, subj_startdate, subj_enddate
	from subject
	where rownum <= #{cri.pageNum} * #{cri.amount}
	) where rn > (#{cri.pageNum} -1) * #{cri.amount}

select c.subj_code,c.ctgr_code,c.subj_nm,c.subj_startdate,c.subj_enddate,a.qst_code,a.qst_content,a.qst_type,a.qst_img,a.qst_url,b.item_code,b.item_content,b.item_img 
from question a,item b,subject c 
where c.subj_code=a.subj_code and a.qst_code = b.qst_code and a.subj_code=#{subj_code}
